# are a and b coprime?
def is_coprime(x, y):
    return gcd(x, y) == 1

# gcd of two positive integers
def gcd(p,q):
    while q != 0:
        p, q = q, p%q
    return p

# set to list converter
def convert(set):
    return list(set)

# outputs unique values from list
def unique(list):
    list_set = set(list)
    unique_list = convert(list_set)
    return unique_list

# sorting string
def sort_string(string):
    r = sorted(string)
    return "".join(r)

# reverse string
def reverse_string(string):
    return string[::-1]

# Convert string to list character-wise
def convert_to_list(string):
    lis=[]
    lis[:0]=string
    return lis

# list of all prime numbers up to n
def seive(n):
    a = []
    prime = [True for i in range(n+1)] 
    p = 2
    while (p * p <= n): 
        if (prime[p] == True): 
            for i in range(p ** 2,n + 1, p): 
                prime[i] = False
        p = p + 1
    for p in range(2,n + 1): 
        if prime[p]: 
            a.append(p)
    return(a)

def counter(a):
    q = [0] * max(a)
    for i in range(len(a)):
        q[a[i] - 1] = q[a[i] - 1] + 1
    return(q)
 
# counts number of elements 
def counter_elements(a):
    q = dict()
    for i in range(len(a)):
        if a[i] not in q:
            q[a[i]] = 0
        q[a[i]] = q[a[i]] + 1
    return(q)

# counts number of letters in string
def string_counter(a):
    q = [0] * 26
    for i in range(len(a)):
        q[ord(a[i]) - 97] = q[ord(a[i]) - 97] + 1
    return(q)
 
# returns factorial of n
def factorial(n,m = 1000000007):
    q = 1
    for i in range(n):
        q = (q * (i + 1)) % m
    return(q)

# returns all (possible) factors of n
def factors(n):
    q = []
    for i in range(1,int(n ** 0.5) + 1):
        if n % i == 0: q.append(i); q.append(n // i)
    return(list(sorted(list(set(q)))))

# returns prime factors of n
def prime_factors(n):
    q = []
    while n % 2 == 0: q.append(2); n = n // 2
    for i in range(3,int(n ** 0.5) + 1,2):
        while n % i == 0: q.append(i); n = n // i
    if n > 2: q.append(n)
    return(list(sorted(q)))

# transpose matrix or vector (list)
def transpose(a):
    n,m = len(a),len(a[0])
    b = [[0] * n for i in range(m)]
    for i in range(m): 
        for j in range(n): 
            b[i][j] = a[j][i]
    return(b)

# returns boolean if x is power of two
def power_two(x):
    return (x and (not(x & (x - 1))))

# returns ceil of division
def ceil(a, b):
    return -(-a // b)
