def isPangram(size,txt):
    if size<26:
        return "NO"
    else:
        txt.upper()
        d = {}
        for i in txt:
            if i in d:
                d[i]+=1
            else:
                d[i]=1
        if len(d)!=26:
            return "YES"
        else:
